import React, { useRef, useState } from "react";
import { motion } from "framer-motion";
import { useWatchlist } from "../hooks/watchlistcontext";

const MovieCard = ({ movie }) => {
  const videoRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);

  const { toggleWatchlist, isInWatchlist } = useWatchlist();
  const inWatchlist = isInWatchlist(movie);

  const handleMouseEnter = () => {
    setIsHovered(true);
    videoRef.current?.play().catch((err) => console.warn("Autoplay error:", err));
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    if (videoRef.current) {
      videoRef.current.pause();
      videoRef.current.currentTime = 0;
    }
  };

  const handleToggleWatchlist = () => {
    toggleWatchlist(movie);
  };

  return (
    <motion.div
      className="movie-card bg-[#1e1e1e] p-4 rounded-xl shadow-md transition-all hover:scale-105 flex flex-col items-center text-center"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      {/* Image or Video Preview */}
      <div className="h-[300px] w-full rounded-lg overflow-hidden mb-2">
        {!isHovered ? (
          <img
            src={movie.posterUrl}
            alt={movie.title}
            className="w-full h-full object-cover object-top rounded-lg"
          />
        ) : (
          <video
            ref={videoRef}
            src={movie.previewUrl}
            className="w-full h-full object-cover rounded-lg"
            muted
            loop
            playsInline
          />
        )}
      </div>

      {/* Title */}
      <h3 className="text-white font-semibold mb-2">{movie.title}</h3>

      {/* Watchlist Button */}
      <button
        onClick={handleToggleWatchlist}
        className={`mt-1 px-4 py-2 rounded-lg text-white transition-all duration-300 ${
          inWatchlist ? "bg-red-500 hover:bg-red-600" : "bg-green-600 hover:bg-green-700"
        }`}
      >
        {inWatchlist ? "Remove from Watchlist" : "Add to Watchlist"}
      </button>
    </motion.div>
  );
};

export default MovieCard;
