import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import MovieCard from "./components/MovieCard";
import WatchlistPage from "./pages/WatchlistPage";
import "./index.css";
import Navbar from "./components/navbar";
import { fetchMoviesBySearch } from "../api/omdb";
import SearchBar from "./SearchBar";



// ðŸ‘‡ 3 sample movie objects
const movies = [
  {
    id: 1,
    title: "Rayan",
    posterUrl: "/rayan.jpeg",
    previewUrl: "/Rayan.mp4",
  },
  {
    id: 2,
    title: "Kuberaa",
    posterUrl: "/kuberaa.jpeg",
    previewUrl: "/Kuberaa.mp4",
  },
  {
    id: 3,
    title: "Vaathi",
    posterUrl: "/vaathi.jpeg",
    previewUrl: "/Vaathi.mp4",
  },
];

// ðŸ‘‡ Home page component that shows movie list
const MovieList = () => {
  return (
    <div className="card-container">
      {movies.map((movie) => (
        <MovieCard key={movie.id} movie={movie} />
      ))}
    </div>
  );
};

// ðŸ‘‡ Main App component with routes
const App = () => {
  return (
    <Router>
        <Navbar/>
      <Routes>
        <Route path="/" element={<MovieList />} />
        <Route path="/watchlist" element={<WatchlistPage />} />
      </Routes>
    </Router>
  );
};

const Home = () => {
  const [movies, setMovies] = useState([]);

  const handleSearch = async (query) => {
    const results = await fetchMoviesBySearch(query);
    setMovies(results);
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      <div className="movie-list">
        {movies.map((movie) => (
          <MovieCard key={movie.imdbID} movie={{
            title: movie.Title,
            posterUrl: movie.Poster,
            previewUrl: "", // no preview from OMDb, leave empty or custom
          }} />
        ))}
      </div>
    </div>
  );
};

export default App;
