// src/components/MovieList.jsx
import React, { useState, useEffect } from "react";
import MovieCard from "./MovieCard";
import "../styles/MovieList.css";

const OMDB_API_KEY = process.env.REACT_APP_OMDB_API_KEY;

const MovieList = ({ query }) => {
  const [movies, setMovies] = useState([]);

  const fetchMovies = async () => {
    try {
      const res = await fetch(`https://www.omdbapi.com/?apikey=${OMDB_API_KEY}&s=${query}`);
      const data = await res.json();
      if (data.Search) {
        const formatted = data.Search.map((movie) => ({
          id: movie.imdbID,
          title: movie.Title,
          posterUrl: movie.Poster,
          previewUrl: "/preview.mp4", // dummy preview
        }));
        setMovies(formatted);
      } else {
        setMovies([]);
      }
    } catch (err) {
      console.error("OMDb fetch error:", err);
    }
  };

  // Inside MovieList.jsx
const [searchInput, setSearchInput] = useState("batman");

// Replace input and add a button
<div className="flex justify-center items-center gap-2 mb-6">
  <input
    type="text"
    value={searchInput}
    onChange={(e) => setSearchInput(e.target.value)}
    placeholder="Search movies..."
    className="px-4 py-2 rounded-md border border-gray-300 w-full max-w-md"
  />
  <button
    onClick={() => setQuery(searchInput)}
    className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
  >
    Search
  </button>
</div>


  return (
    <div className="movie-list-container">
      <div className="movie-scroll-container">
        {movies.length === 0 ? (
          <p className="text-white">No movies found.</p>
        ) : (
          movies.map((movie) => <MovieCard key={movie.id} movie={movie} />)
        )}
      </div>
    </div>
  );
};

export default MovieList;
