import React, { useRef, useState, useEffect } from "react";
import { motion } from "framer-motion";
import { fetchTrailerUrl } from "../utils/fetchMovieTrailer";
import "../styles/MovieCard.css";
import { useWatchlist } from "../hooks/watchlistcontext";

const MovieCard = ({ movie }) => {
  const videoRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);
  const [trailerUrl, setTrailerUrl] = useState(null);

  const { toggleWatchlist, isInWatchlist } = useWatchlist();
  const inWatchlist = isInWatchlist(movie);

  useEffect(() => {
    fetchTrailerUrl(movie.title).then(setTrailerUrl);
  }, [movie.title]);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  return (
    <motion.div
      className="movie-card"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="media-container">
        {!isHovered || !trailerUrl ? (
          <img
            src={movie.posterUrl}
            alt={movie.title}
            className="poster-img"
          />
        ) : (
          <iframe
            width="100%"
            height="100%"
            src={trailerUrl + "?autoplay=1&mute=1"}
            frameBorder="0"
            allow="autoplay; encrypted-media"
            allowFullScreen
            title="Trailer"
          ></iframe>
        )}
      </div>
      <h3>{movie.title}</h3>
      <button onClick={() => toggleWatchlist(movie)}>
        {inWatchlist ? "Remove from Watchlist" : "Add to Watchlist"}
      </button>
    </motion.div>
  );
};

export default MovieCard;
