// src/components/SearchBar.jsx
import React, { useState } from "react";

const SearchBar = ({ onSearch }) => {
  const [query, setQuery] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (query.trim()) onSearch(query);
  };

  const handleChange = (e) => {
    setQuery(e.target.value);
  };

  return (
    <div className="w-full flex justify-center mt-6 mb-4 px-4">
      <form
  onSubmit={handleSubmit}
  className="w-full flex justify-center items-center my-6 gap-4"
>
  <input
    type="text"
    value={query}
    onChange={(e) => setQuery(e.target.value)}
    placeholder="Search movies..."
    className="px-4 py-2 rounded-md border border-gray-300 w-[250px] sm:w-[400px] focus:outline-none"
  />
  <button
    type="submit"
    className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
  >
    Search
  </button>
</form>

    </div>
  );
};

export default SearchBar;
