import React, { useRef, useState } from "react";
import { motion } from "framer-motion";
import "../styles/MovieCard.css";
import { useWatchlist } from "../hooks/useWatchlist";


const { toggleWatchlist, isInWatchlist } = useWatchlist(); // ðŸ‘ˆ use the hook
const inWatchlist = isInWatchlist(movie);


const MovieCard = ({ movie }) => {
  const videoRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
    videoRef.current?.play();
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    videoRef.current?.pause();
    videoRef.current.currentTime = 0;
  };
  

  return (
    <motion.div
      className="movie-card"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="media-container">
        {!isHovered ? (
          <img src={movie.posterUrl} alt={movie.title} className="poster-img" style={{ width: "100%", height: "100%", objectFit: "cover" }} />
        ) : (
          <video
            ref={videoRef}
            src={movie.previewUrl}
            className="preview-video"
            muted
            loop
            playsInline
          />
        )}
      </div>
      <h3>{movie.title}</h3>
    </motion.div>
  );
};

export default MovieCard;
