import React, { useRef, useState } from "react";
import { motion } from "framer-motion";
import "../styles/MovieCard.css";
import { useWatchlist } from "../hooks/watchlistcontext";

const MovieCard = ({ movie }) => {
  const videoRef = useRef(null);
  const [isHovered, setIsHovered] = useState(false);

  const { toggleWatchlist, isInWatchlist } = useWatchlist();
  const inWatchlist = isInWatchlist(movie);

  const handleMouseEnter = () => {
    setIsHovered(true);
    videoRef.current?.play().catch((err) => console.warn("Autoplay error:", err));
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
    if (videoRef.current) {
      videoRef.current.pause();
      videoRef.current.currentTime = 0;
    }
  };

  const handleToggleWatchlist = () => {
    toggleWatchlist(movie); // âœ… only pass once
  };

  return (
    <motion.div
      className="movie-card"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div
        className="media-container"
        style={{
          height: "300px",
          borderRadius: "10px",
          overflow: "hidden",
        }}
      >
        {!isHovered ? (
          <img
  src={movie.posterUrl}
  alt={movie.title}
  className="poster-img"
  style={{
    width: "100%",
    height: "100%",
    objectFit: "cover",
    objectPosition: "top", // ðŸ”¥ Ensures face not cut
    borderRadius: "10px",
  }}
/>

        ) : (
          <video
            ref={videoRef}
            src={movie.previewUrl}
            className="preview-video"
            muted
            loop
            playsInline
          />
        )}
      </div>

      <h3 style={{ color: "white", marginTop: "0.5rem" }}>{movie.title}</h3>

      <button
        onClick={handleToggleWatchlist} // âœ… fixed
        style={{
          marginTop: "0.5rem",
          padding: "8px 12px",
          borderRadius: "10px",
          border: "none",
          backgroundColor: inWatchlist ? "#ff4d4d" : "#4caf50",
          color: "white",
          cursor: "pointer",
          transition: "all 0.3s ease",
        }}
      >
        {inWatchlist ? "Remove from Watchlist" : "Add to Watchlist"}
      </button>
    </motion.div>
  );
};

export default MovieCard;
