import React, { useState, useEffect } from "react";
import MovieCard from "./MovieCard";
import "../styles/MovieList.css";

const OMDB_API_KEY = process.env.REACT_APP_OMDB_API_KEY;

const MovieList = () => {
  const [searchInput, setSearchInput] = useState("batman");
  const [query, setQuery] = useState("batman");
  const [movies, setMovies] = useState([]);

  const fetchMovies = async () => {
    try {
      const res = await fetch(`https://www.tmdbapi.com/?apikey=${TMDB_API_KEY}&s=${query}`);
      const data = await res.json();
      if (data.Search) {
        const formatted = data.Search.map((movie) => ({
          id: movie.imdbID,
          title: movie.Title,
          posterUrl: movie.Poster,
          previewUrl: "/preview.mp4", // dummy preview
        }));
        setMovies(formatted);
      } else {
        setMovies([]);
      }
    } catch (err) {
      console.error("OMDb fetch error:", err);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, [query]);

  return (
    <div className="movie-list-container">
      {/* Search Bar */}
      <div className="search-bar-wrapper">
        <input
          type="text"
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
          placeholder="Search movies..."
          className="search-input"
        />
        <button onClick={() => setQuery(searchInput)} className="search-button">
          Search
        </button>
      </div>

      {/* Movie Cards */}
      <div className="movie-scroll-container">
        {movies.length === 0 ? (
          <p className="text-white">No movies found.</p>
        ) : (
          movies.map((movie) => <MovieCard key={movie.id} movie={movie} />)
        )}
      </div>
    </div>
  );
};

export default MovieList;
